import React from 'react';
import APIResponseErrorMessage from "../commons/errorhandling/api-response-error-message";
import {
    Button,
    Card,
    CardHeader,
    Col,
    Modal,
    ModalBody,
    ModalHeader,
    Row
} from 'reactstrap';
 
//import * as DOCTOR_API from "./api/doctor-api"
//import * as PATIENT_API from "./api/patient-api"
//import * as CAREGIVER_API from "./api/caregiver-api"
import * as USER_API from "../userapi/user-api"
import UserTable from './userTable'
  


class DoctorHome extends React.Component {

    constructor(props) {
        super();
        super(props);
      
        this.state = {
            insertDoctor: false,
            insertPatient: false,
            insertCaregiver: false,

            collapseForm: false,
            tableData: [],
            isLoaded: false,
            errorStatus: 0,
            error: null
        };

        this.toggleForm = this.toggleForm.bind(this);
        this.reload = this.reload.bind(this);
    }

    componentDidMount() {
        this.fetchUsers();
    }

    fetchUsers() {
        return USER_API.getUsers((result, status, err) => {

            if (result !== null && status === 200) {
                this.state.tableData = result;
                this.state.isLoaded = true;
                this.setState(this.state);
            } else {
                this.state.errorStatus = status;
                this.state.error = err;
                this.setState(this.state);
            }
        });
    }

    toggleInsertDoctorForm() {
         this.state.insertDoctor = !this.state.insertDoctor;
        this.setState(this.state);
    }


    reload() {
        this.state.isLoaded = false;
        this.setState(this.state);
        this.toggleForm();
        this.fetchUsers();
    }

    render() {
        return (
            <div>
                <CardHeader>
                    <strong> Users Management </strong>
                </CardHeader>
                <Card>
                    <br/>
                    <Row>
                        <Col sm={{size: '8', offset: 1}}>
                        <Button color = "primary"  onClick={this.toggleForm}>  Add doctor </Button>
                        <Button color = "primary"   onClick={this.toggleForm}>  Add patient </Button>
                        <Button color = "primary"   onClick={this.toggleForm}>  Add caregiver </Button>

                        </Col>
                    </Row>
                    <br/>
                    <Row>
                        <Col sm={{size: '8', offset: 1}}>
                            {this.state.isLoaded && <UserTable tableData = {this.state.tableData}/>}
                            {this.state.errorStatus > 0 && <APIResponseErrorMessage
                                                            errorStatus={this.state.errorStatus}
                                                            error={this.state.error}
                                                        />   }
                        </Col>
                    </Row>
                </Card>

              

            </div>
        )

    }
}


export default DoctorHome;
