import React from 'react';
import validate from "./validators/patient-validators";
import Button from "react-bootstrap/Button";
 import APIResponseErrorMessage from "../commons/errorhandling/api-response-error-message";
import {Col, Row} from "reactstrap";
import { FormGroup, Input, Label} from 'reactstrap';
import { Redirect } from 'react-router';
import * as PATIENT_API from "./api/patient-api";
import * as CAREGIVER_API from "../caregiver/api/caregiver-api";

import {
    Card,
    CardHeader,
    Modal,
    ModalBody,
    ModalHeader
} from 'reactstrap';
 
class PatientPage extends React.Component {

    constructor(props) {
        super(props);
        
        this.state = {
redirect : false,
redirectTo : "/",
patientId : props.match.params.id,
patient : null,
caregiver: null,
isLoadedCaregiver : false,
isLoaded : false,
errorStatus : 0,
error : "",
showMessage : false,
messsage : "",
formIsValid: false,
formControls: {
    
    
    name: {
        value: '',
        placeholder: 'What is the patient\'s name?',
        valid: false,
        touched: false,
        validationRules: {
            minLength: 3,
            isRequired: false
        }
    },
    username: {
        value: '',
        placeholder: 'What is the patient\'s Username?',
        valid: false,
        touched: false,
        validationRules: {
            minLength: 3,
            isRequired: true
        }
    },
    password: {
        value: '',
        placeholder: 'What is the patient\'s Password?',
        valid: false,
        touched: false,
        validationRules: {
            minLength: 3,
            isRequired: false
